from fastapi import APIRouter, Depends, HTTPException from pydantic import BaseModel from datetime import datetime, date
from ...core.security import get_current_active_user from ...models.user import User from ...services.ai_service import ai_service
router = APIRouter()
class RevenueReportRequest(BaseModel): start_date: date end_date: date
@router.post("/generate-revenue-report") async def generate_revenue_report( request: RevenueReportRequest, current_user: User = Depends(get_current_active_user) ): """ Tạo báo cáo doanh thu bằng AI (phạm vi tài khoản đang đăng nhập) """ try: report = ai_service.generate_revenue_report( start_date=request.start_date.strftime('%Y-%m-%d'), end_date=request.end_date.strftime('%Y-%m-%d'), owner_id=current_user.owner_id )
return { "report": report, "period": f"{request.start_date} đến {request.end_date}", "timestamp": datetime.now() } except Exception as e: raise HTTPException(status_code=500, detail=f"Lỗi AI service: {str(e)}")

from fastapi import APIRouter, Depends, HTTPException from pydantic import BaseModel from datetime import datetime, date
from ...core.security import get_current_active_user from ...models.user import User from ...services.ai_service import ai_service
router = APIRouter()
class RevenueReportRequest(BaseModel): start_date: date end_date: date
@router.post("/generate-revenue-report") async def generate_revenue_report( request: RevenueReportRequest, current_user: User = Depends(get_current_active_user) ): """ Tạo báo cáo doanh thu bằng AI (phạm vi tài khoản đang đăng nhập) """ try: report = ai_service.generate_revenue_report( start_date=request.start_date.strftime('%Y-%m-%d'), end_date=request.end_date.strftime('%Y-%m-%d'), owner_id=current_user.owner_id )
return { "report": report, "period": f"{request.start_date} đến {request.end_date}", "timestamp": datetime.now() } except Exception as e: raise HTTPException(status_code=500, detail=f"Lỗi AI service: {str(e)}")

from datetime import datetime, timedelta
from typing import Optional
from jose import JWTError, jwt
from passlib.context import CryptContext
from fastapi import Depends, HTTPException, status
from fastapi.security import OAuth2PasswordBearer
from sqlalchemy.orm import Session
from .config import settings
from .database import get_db
from ..models.user import User

pwd_context = CryptContext(schemes=["bcrypt"], deprecated="auto")
# Point to v2 login endpoint for OAuth2 docs/consumers
oauth2_scheme = OAuth2PasswordBearer(tokenUrl="/api/v2/auth/login")

def verify_password(plain_password, hashed_password):
    return pwd_context.verify(plain_password, hashed_password)

def get_password_hash(password):
    return pwd_context.hash(password)

def get_user(db: Session, email: str):
    return db.query(User).filter(User.email == email).first()

def get_user_by_id(db: Session, owner_id: int):
    return db.query(User).filter(User.owner_id == owner_id).first()

def authenticate_user(db: Session, email: str, password: str):
    user = get_user(db, email)
    if not user:
        return False
    if not verify_password(password, user.password):
        return False
    return user

def create_access_token(data: dict, expires_delta: Optional[timedelta] = None):
    to_encode = data.copy()
    if expires_delta:
        expire = datetime.utcnow() + expires_delta
    else:
        expire = datetime.utcnow() + timedelta(minutes=15)
    to_encode.update({"exp": expire})
    encoded_jwt = jwt.encode(to_encode, settings.secret_key, algorithm=settings.algorithm)
    return encoded_jwt

async def get_current_user(token: str = Depends(oauth2_scheme), db: Session = Depends(get_db)):
    credentials_exception = HTTPException(
        status_code=status.HTTP_401_UNAUTHORIZED,
        detail="Could not validate credentials",
        headers={"WWW-Authenticate": "Bearer"},
    )
    try:
        payload = jwt.decode(token, settings.secret_key, algorithms=[settings.algorithm])
        email: Optional[str] = payload.get("sub")
        owner_id: Optional[int] = payload.get("oid")
        if owner_id is not None:
            user = get_user_by_id(db, owner_id=owner_id)
        elif email is not None:
            user = get_user(db, email=email)
        else:
            raise credentials_exception
    except JWTError:
        raise credentials_exception
    if user is None:
        raise credentials_exception
    return user

async def get_current_active_user(current_user: User = Depends(get_current_user)):
    if not current_user.is_active:
        raise HTTPException(status_code=400, detail="Inactive user")
    return current_user

def require_role(required_role: str):
    """Decorator to check if user has required role"""
    async def role_checker(current_user: User = Depends(get_current_active_user), db: Session = Depends(get_db)):
        # Load role relationship if not already loaded
        if not hasattr(current_user, 'role') or current_user.role is None:
            db.refresh(current_user, ['role'])

        if current_user.role.authority != required_role:
            raise HTTPException(
                status_code=status.HTTP_403_FORBIDDEN,
                detail=f"Access denied. Required role: {required_role}"
            )
        return current_user
    return role_checker

